import logo from './logo.svg';
import './App.css';
import { Container, Row, Col, Card, Table, Button} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';

function App() {

  const [mydata, setData] = useState([]);
  const apiget = () => {
    fetch('https://inshorts.me/news/all?offset=0&limit=21')
      .then((Response) => Response.json())
      .then((json) => {
        console.log(json);
        setData(json.data.articles)
      });
  };

  useEffect(() => {
    apiget();
    const interval = setInterval(() => { apiget(); }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Container fluid className='main-container' style={{padding:'15px', maxWidth:'1200px'}}>
      <Row xs={1} sm={2} md={2} lg={3} className="g-4">
        {
          mydata.map(
            (value) => {
              return (
                <>
                  <Col className='container-fluid mt-4'>
                    <Card style={{ width: '100%', height:'700px', border:'solid 1px #e1e1e1', boxShadow:'0px 3px 5px' }}>
                      <Card.Img variant="top" src={value.imageUrl} style={{width: 'auto', height:'400px', borderRadius:'15px', image}}/>
                      <Card.Body>
                        <Card.Title>{value.title}</Card.Title>
                        <Card.Text>
                          {value.content}
                        </Card.Text>
                        <footer className='blockquote-footer'>
                          Source Name : {value.sourceName}
                        </footer>
                      </Card.Body>
                    </Card>
                  </Col>
                </>
              )
            }
          )
        }
      </Row>
    </Container>
  );
}

export default App;
