import './App.css';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigationbar from './components/Navbar';
import Top from './pages/top';
import Trending from './pages/trending';
import Science from './pages/science';
import Entertainment from './pages/entertainment';
import Sports from './pages/sports';


function App() {

  const [mydata, setData] = useState([]);
  const apiget = () => {
    fetch('https://inshorts.me/news/all?offset=0&limit=99')
      .then((Response) => Response.json())
      .then((json) => {
        console.log(json);
        setData(json.data.articles)
      });
  };

  useEffect(() => {
    apiget();
    const interval = setInterval(() => { apiget(); }, 50000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>

      <Router>
        <Navigationbar />
        <Routes>
          <Route path="/top" component={Top} />
          <Route path="/trending" component={Trending} />
          <Route path="/science" component={Science} />
          <Route path="/entertainment" component={Entertainment} />
          <Route path="/sports" component={Sports} />
        </Routes>
      </Router>
      {/* <Router>
        <Navigationbar />
        <Switch>
          <Route path="/top" component={Top} />
          <Route path="/trending" component={Trending} />
          <Route path="/science" component={Science} />
          <Route path="/entertainment" component={Entertainment} />
          <Route path="/sports" component={Sports} />
        </Switch>
      </Router> */}
      <Container fluid className='main-container'>
        <Row xs={1} sm={2} md={2} lg={3} className="g-4">
          {
            mydata.map(
              (value) => {
                return (
                  <>
                    <Col className='container-fluid mt-4'>
                      <Card className='news-card'>
                        <Card.Img className='card-img' variant="top" src={value.imageUrl} />
                        <Card.Body className='main-content'>
                          <p className='news-source'>{value.sourceName}</p>
                          <Card.Title className='news-heading'>{value.title}</Card.Title>
                          <Card.Text className='news-sub-title'>
                            {value.subtitle}
                          </Card.Text>
                        </Card.Body>
                      </Card>
                    </Col>
                  </>
                )
              }
            )
          }
        </Row>
      </Container>
    </>

  );
}

export default App;
